  // Giờ mà điền hết vào thì rất tốn thời gian
  // Nên ta sẽ sử dụng map
  const line = [0, 1, 2];
  for (let i = 0; i < 3; i++) {
    const [a, b, c] = [i];
    console.log(a, b, c);
  }
  return (
    <div className="game-board">
      {Array(9)
        .fill()
        .map((item, index) => (
          <Cell key={index}></Cell>
        ))}
    </div>
  );
};

export default Board;
