import logo from "./logo.svg";
import "./App.css";

// JSX : Javascript XML
// ES6

/**
 * element = <div id="root">Hello World</div>
 * element = React.createElement('div', {id: 'root'}, 'Hello World')
 * function createElement(elementType, props, ...children)
 * elementType: 'div', 'p' , 'span'
 * props: những cái attribute của thẻ VD: className,id,href,src,...
 * ...children:
 * element2 = (<div>
 * <span>Hello</span> <span>World</span>
 * </div>)
 * element2 = React.createElement('div',{
 * children:[
 * React.createElement('span', null, 'Hello'),
 * ' ',
 * React.createElement('span',null, 'World')
 * ]
 * })
 */

function App() {
  const name = "Khoi";
  function fullName(firstName, lastName) {
    return `${firstName} ${lastName}`;
  }
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Hello {name === "Khoi" ? "Tofu" : "Unknown"}</h1>
        <h2>{fullName("Tran Dang", "Khoi")}</h2>
        {/* <p>
          Edit <code>src/App.js</code> and save to reload.
        </p> */}
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
