import React from "react";
// import "./Button.scss";
import styles from "./Button.module.css"; // style giờ là một object và key của nó là class bên trong module css
const Button = (props) => {
  return (
    <>
      <button
        className={`${styles.button} ${
          props.secondary ? styles.buttonSecondary : ""
        }`}
      >
        {props.children}
      </button>
    </>
  );
};

export default Button;
