import React from "react";
import styled from "styled-components";

/*
 * const StyledCard = styled.tag(h1,h2,div,span,...)``
 * CSS-in-JS : CSX
 */

const StyledCard = styled.div`
  position: relative;
`;

const CardImageWrapper = styled.div`
  width: 400px;
  height: 400px;
  border-radius: 8px;
  overflow: hidden;
`;

const CardImage = styled.img`
  display: block;
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: inherit;
`;

const CardContent = styled.div`
  position: absolute;
  left: 50%;
  bottom: 0;
  transform: translateX(-50%);
  background-color: white;
  z-index: 10;
  border-radius: 20px;
  padding: 20px;
`;

const CardTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
`;

const Card = () => {
  return (
    <StyledCard>
      <CardImageWrapper>
        <CardImage
          src="https://cdn.dribbble.com/users/2400293/screenshots/15883991/media/c5a6d6098f4ec6a0ce1b38909d82a494.png?compress=1&resize=1000x750&vertical=top"
          alt=""
        />
      </CardImageWrapper>
      <CardContent>
        <CardTop>
          <div>
            <img
              src="https://cdn.dribbble.com/users/2400293/screenshots/14988700/media/5fd7eb67da21caa45eb864fbc27f24a2.jpg?compress=1&resize=1000x750&vertical=top"
              alt=""
            />
            <span>@toilatofu</span>
          </div>
          <div>256</div>
        </CardTop>
        <div>
          <h3>Cosmic Perspective</h3>
          <span>12,000 PSL</span>
        </div>
      </CardContent>
    </StyledCard>
  );
};

export default Card;
