import React from "react";
import { winnerCalculate } from "../helper";
import Cell from "./Cell";

const Board = (props) => {
  // Array.fill(9) -> [undefined,undefined,...]
  // Giờ mà điền hết vào thì rất tốn thời gian
  // Nên ta sẽ sử dụng map
  // console.log(props);
  // const cells = ["X", null, null, "X", null, null, "X", null, null];
  // console.log(winnerCalculate(cells));
  return (
    <div className="game-board">
      {props.cells.map((item, index) => (
        <Cell
          key={index}
          value={item}
          onClick={() => props.onClick(index)} // onClick ở đây (onclick bên trái) là props thuộc component Cells
        ></Cell>
      ))}
    </div>
  );
};

export default Board;
